import React, { useState } from 'react';
// import { Link} from 'react-router-dom';
import {Link, useHistory } from 'react-router-dom'; // Make sure you import Link
import './Login.css';
import { useToken } from './TokenContext';


const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const {  setToken } = useToken();



  const handleLogin = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('http://localhost:8000/api/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: username,
          password: password,
        }),
      });
      usehistory.push("/");

      if (response.status === 200) {
        const data = await response.json();
        console.log('Login successful. Token:', data);
        setToken(data)

        // Handle success, maybe store token in state or local storage
      } else {
        console.error('Login failed');
        // Handle failure, show an error message
      }
    } catch (error) {
      console.error('Error:', error);
      // Handle error, show an error message
    }
  };



  return (
    <div>
      {/* Navigation Bar */}
      <div className="nav-bar">
        <Link to="/" className="nav-item">Dashboard</Link>
        <Link to="/admin" className="nav-item">Admin</Link>
        <Link to="/apply" className="nav-item">Apply Leave</Link>
        <Link to="/leavestatus" className="nav-item">Leave Status</Link>
        {/* <Link to="/login" className="nav-item">Login</Link> */}
        <div className="navbar-title">Leave Management System</div>
        <div className='logout-div'>
          <div className="nav-item">
            <span className="logout-icon"></span>Logout
          </div>
        </div>
      </div>

      {/* Login Form */}
      <div className="login-box">
        <h2>Login</h2>
        <form>
          <div className="user-box">
            <input
              type="text"
              name=""
              required
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <label>Username</label>
          </div>
          <div className="user-box">
            <input
              type="password"
              name=""
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <label>Password</label>
          </div>
          <button className="login-button" href="#" onClick={handleLogin}>
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
