import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './LeaveForm.css';

const Leavestatus = () => {
    const [leavedata, setLeaveData] = useState([]);
    const [employee, setEmployee] = useState([]);
    const [selectedEmployee, setSelectedEmployee] = useState(null);
    const [empleavedata, setEmpLeaveData] = useState([]);


    useEffect(() => {
        fetchEmployees();
        fetchleaves();
      }, []);

      const fetchEmployees = async () => {
        try {
          const response = await axios.get('http://localhost:8000/lms/employee/');
          setEmployee(response.data);
        } catch (error) {
          console.error('Error fetching employees:', error);
        }
      };

      const fetchleaves = async () => {
        try {
          const response = await axios.get('http://localhost:8000/lms/apply/');
          setLeaveData(response.data);
        } catch (error) {
          console.error('Error fetching employees:', error);
        }
      };
      const handleEmployeeChange = async (e) => {
        const selectedEmpId = parseInt(e.target.value);
        const selectedEmp = employee.find((emp) => emp.id === selectedEmpId);
        console.log(selectedEmp)
        setSelectedEmployee(selectedEmp);
        const requested_leave = leavedata.filter(leave => leave.emp === Number(selectedEmpId))
        console.log(selectedEmployee)
        setEmpLeaveData(requested_leave) 
      }; 

      const deleteleave = async (e)  => {
        try {
          const response = await axios.delete(`http://localhost:8000/lms/update/${e.target.value}`);
          alert('Le')
        } catch (error) {
          console.error('Error fetching employees:', error);
        }
      }

      return(
      <div>
        <select onChange={handleEmployeeChange}>
        <option value="none">Select Employee</option>
        {employee.map((employee) => (
          <option key={employee.id} value={employee.id}>
            {employee.emp_name}
          </option>
          ))}
           </select>
          
        {empleavedata && (
        <div className="submitted-data-container">
          <div className="submitted-data-box">

            <h3>Leave Data</h3>
            {/* Changes start here */}
            <table>
              <thead>
                <tr>
                  <th>Employee Name</th>
                  <th>Employee ID</th>
                  {/* <th>Available Leaves</th> */}
                  <th>Leave Type</th>
                  <th>Start Date</th>
                  <th>End Date</th>
                  <th>Number of Days</th>
                  <th>Reason</th>
                  <th>Status</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
  
                    {/* <td>{selectedEmployee.emp_name}</td>
                    <td>{selectedEmployee.emp_id}</td> */}
                    {/* <td>{empleavedata.available_leaves}</td> */}
                  {empleavedata.map((leave) => (
                  <tr key={leave.id}>
                    <td>{selectedEmployee.emp_name}</td>
                    <td>{selectedEmployee.emp_id}</td>
                    <td>{leave.leave_type}</td>
                    <td>{leave.start_date}</td>
                    <td>{leave.end_date}</td>
                    <td>{leave.days}</td>
                    <td>{leave.reason}</td>
                    <td>{leave.status}</td>
                    <td><button onClick={deleteleave(leave.id)}>Delete</button></td>
                  </tr>
                ))}
              </tbody>
            </table>
            {/* Changes end here */}
          </div>
        </div>
      )}

          </div>
      )
};

export default Leavestatus;
