import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Make sure to import Link from React Router
import './Register.css';
import axios from 'axios';

function Register() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [designation, setDesignation] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleRegister = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/register/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: username,
          email: email,
          password: password,
        }),
      });

      if (response.status === 201) {
        console.log('Registration successful');
        // Handle success, maybe redirect to a different page
      } else {
        console.error('Registration failed');
        // Handle failure, show an error message
      }
    } catch (error) {
      console.error('Error:', error);
      // Handle error, show an error message
    }
    const employeedata = {
      emp_name: username,
      emp_email: email,
      designation: designation
    }

    try {
      const response = await axios.post(
        'http://localhost:8000/lms/employee/',
        employeedata
      );
      console.log('Data submitted successfully:', response.data);
      // alert('Data submitted successfully');
      notification.success({
        message:'Leave Request Submitted',
        description:'For further updates check leave status'
      })
    } catch (error) {
      console.error('Error submitting data:', error);

    }
  };

  return (
    <div>
      <div className="nav-bar">
        <Link to="/" className="nav-item">Dashboard</Link>
        <Link to="/admin" className="nav-item">Admin</Link>
        <Link to="/apply" className="nav-item">Apply Leave</Link>
        <Link to="/leavestatus" className="nav-item">Leave Status</Link>
        {/* <Link to="/login" className="nav-item">Login</Link> */}
        <div className="navbar-title">Leave Management System</div>
        <div className='logout-div'>
          <div className="nav-item">
            <span className="logout-icon"></span>Logout
          </div>
        </div>
      </div>
      <div className="register-container">
        <h2>Register</h2>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="designation"
          placeholder="designation"
          value={designation}
          onChange={(e) => setDesignation(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <input
          type="password"
          placeholder="Confirm Password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
        />
        <button onClick={handleRegister}>Register</button>
      </div>
    </div>
  );
}

export default Register;
