import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Leav.css'; 

function LeaveApplication() {
  const [employeeId, setEmployeeId] = useState('');
  const [leaveTypeId, setLeaveTypeId] = useState('');
  const [days, setDays] = useState('');
  const [leaveTypes, setLeaveTypes] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState(null);

  useEffect(() => {
    fetchLeaveTypes();
    fetchEmployees();
  }, []);

  const fetchLeaveTypes = async () => {
    try {
      const response = await axios.get('http://localhost:8000/lms/leave_type/');
      setLeaveTypes(response.data);
    } catch (error) {
      console.error('Error fetching leave types:', error);
    }
  };

  const fetchEmployees = async () => {
    try {
      const response = await axios.get('http://localhost:8000/lms/employee/');
      setEmployees(response.data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  const handleEmployeeChange = (e) => {
    const selectedEmpId = e.target.value;
    setEmployeeId(selectedEmpId);
    setSelectedEmployee(employees.find((employee) => employee.id === parseInt(selectedEmpId)));
  };

  const handleApplyLeave = async () => {
    // ... Rest of the code for leave application ...

    // After applying leave, you can clear the selected employee details
    setSelectedEmployee(null);
  };

  return (
    <div>
      <nav className="navbar">
        {selectedEmployee && (
          <div className="employee-details">
            <p><strong>Name:</strong> {selectedEmployee.emp_name}</p>
            <p><strong>Employee ID:</strong> {selectedEmployee.emp_id}</p>
            <p><strong>Designation:</strong> {selectedEmployee.designation}</p>
          </div>
        )}
      </nav>
      <h2>Apply for Leave</h2>
      {/* Rest of the form elements and button */}
    </div>
  );
}

export default LeaveApplication;
