import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './LeaveForm.css';

const Leavestatus = () => {
    const [leavedata, setLeaveData] = useState([]);
    const [employee, setEmployee] = useState([]);
    const [selectedEmployee, setSelectedEmployee] = useState(null);
    const [empleavedata, setEmpLeaveData] = useState([]);


    useEffect(() => {
        fetchEmployees();
        fetchleaves();
      }, []);

      const fetchEmployees = async () => {
        try {
          const response = await axios.get('http://localhost:8000/lms/employee/');
          setEmployee(response.data);
        } catch (error) {
          console.error('Error fetching employees:', error);
        }
      };

      const fetchleaves = async () => {
        try {
          const response = await axios.get('http://localhost:8000/lms/apply/');
          setLeaveData(response.data);
        } catch (error) {
          console.error('Error fetching employees:', error);
        }
      };
      const handleEmployeeChange = async (e) => {
        const selectedEmpId = e.target.value;
        // console.log(selectedEmpId)
        // setEmployeeId(selectedEmpId);
        setSelectedEmployee(employee.find((employee) => employee.id === parseInt(selectedEmpId)));
        const requested_leave = leavedata.filter(leave => leave.emp === Number(selectedEmpId))
        console.log(requested_leave[0])
        setEmpLeaveData(requested_leave[0])
        // try {
        //   const response = await axios.get(`http://localhost:8000/lms/update/${requested_leave[0]['id']}`);
        //   setEmpLeaveData(response.data);
        //   console.log(leavedata)
        // } catch (error) {
        //   console.error('Error fetching employees:', error);
        // }
       
      }; 
      return(
      <div>
        <select onChange={handleEmployeeChange}>
        <option value="none">Select Employee</option>
        {employee.map((employee) => (
          <option key={employee.id} value={employee.id}>
            {employee.emp_name}
          </option>
          ))}
           </select>
          
        {empleavedata && (
        <div className="submitted-data-container">
          <div className="submitted-data-box">

            <h3>Leave Data</h3>
            {/* Changes start here */}
            <table>
              <thead>
                <tr>
                  <th>Employee Name</th>
                  <th>Employee ID</th>
                  {/* <th>Available Leaves</th> */}
                  <th>Leave Type</th>
                  <th>Start Date</th>
                  <th>End Date</th>
                  <th>Reason</th>
                </tr>
              </thead>
              <tbody>
                  <tr>
                    <td>{selectedEmployee.available_l}</td>
                    <td>{empleavedata.available_leaves}</td>
                    <td>{empleavedata.available_leaves}</td>
                    <td>{empleavedata.leave_type}</td>
                    <td>{empleavedata.start_date}</td>
                    <td>{empleavedata.end_date}</td>
                    <td>{empleavedata.reason}</td>
                    <td>{empleavedata.days}</td>
                    <td>{empleavedata.status}</td>
                  </tr>
              </tbody>
            </table>
            {/* Changes end here */}
          </div>
        </div>
      )}

          </div>
      )
};

export default Leavestatus;
