import {React,useState, useEffect} from 'react';
import './Dashboard.css';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { ResponsiveContainer, BarChart, Bar, XAxis, YAxis, CartesianGrid, Legend, Tooltip } from 'recharts';
import Navbar from './Navbar';






const Dashboard = () => {

  const [leavedata, setLeaveData] = useState([]);
  const [leavetypedata, setLeaveTypeData] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [empleavedata, setEmpLeaveData] = useState(null);
  const [employeeId, setEmployeeId] = useState('');


  useEffect(() => {
      fetchEmployees();
      fetchleaves();
      fetchleave_types();
    }, []);

    const fetchEmployees = async () => {
      try {
        const response = await axios.get('http://localhost:8000/lms/employee/');
        setEmployees(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    const fetchleaves = async () => {
      try {
        const response = await axios.get('http://localhost:8000/lms/apply/');
        setLeaveData(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    const fetchleave_types = async () => {
      try {
        const response = await axios.get('http://localhost:8000/lms/leave_type/');
        setLeaveTypeData(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    const handleEmployeeChange = async (e) => {
      const selectedEmpId = e.target.value;
      setEmployeeId(selectedEmpId);
      setSelectedEmployee(employees.find((employee) => employee.id === parseInt(selectedEmpId)));
      const requested_leave = leavetypedata.filter(leave => leave.employee === Number(selectedEmpId))
      setEmpLeaveData(requested_leave) 
      // console.log(empleavedata[0]["annual_leave"])

    };

    function bar(){
      if(empleavedata){
        const data = [

          { name: 'Annual Leave', leave_taken: 12 - empleavedata[0].annual_leave, Annual_leave: empleavedata[0].annual_leave },
          { name: 'Sick Leave', leave_taken: 5 - empleavedata[0].sick_leave , Sick_leave: empleavedata[0].sick_leave },
          { name: 'Bereavement Leave', leave_taken: 5 - empleavedata[0].bereavement_leave, Bereavement_leave_leave: empleavedata[0].bereavement_leave },
          { name: 'Maternity Leave', leave_taken: 15 - empleavedata[0].maternity_leave, Maternity_leave: empleavedata[0].maternity_leave },
          { name: 'Ma Leave', leave_taken: 15 - empleavedata[0].maternity_leave, Maternity_leave: empleavedata[0].maternity_leave },
          { name: 'Maternity Leave', leave_taken: 15 - empleavedata[0].maternity_leave, Maternity_leave: empleavedata[0].maternity_leave },
          { name: 'Maternity Leave', leave_taken: 15 - empleavedata[0].maternity_leave, Maternity_leave: empleavedata[0].maternity_leave },
        ];
      }
      const data = [

        { name: 'Annual Leave', leave_taken: 3, Paternity_leave: 8 },
        { name: 'Sick Leave', leave_taken: 3, Sick_leave: 9 },
        { name: 'Casual Leave', leave_taken: 3, Casual_leave: 9 },
        { name: 'Total Leave', leave_taken: 3, Total_leave: 15 },
      ];

      return data
    }


    function totalleaves(){
      const sumValues = empleavedata[0].annual_leave + empleavedata[0].sick_leave + empleavedata[0].bereavement_leave + empleavedata[0].paternity_leave + empleavedata[0].maternity_leave + empleavedata[0].study_leave
      return sumValues
    }


  return (
    <div className="dashboard-container">
        <div className="nav-bar">
        <Link to="/" className="nav-item">Dashboard</Link>
        <Link to="/admin" className="nav-item">Admin</Link>
        <Link to="/apply" className="nav-item">Apply Leave</Link>
        <Link to="/leavestatus" className="nav-item">Leave Status</Link>
        <div className="navbar-title">Leave Management System</div>
        <div className='logout-div'>
        <select value={employeeId} onChange={handleEmployeeChange} className='select'>
          <option value="none">Select Employee</option>
          {employees.map((employee) => (
            <option key={employee.id} value={employee.id}>
              {employee.emp_name}
            </option>
            ))}
             </select>
        <div className="nav-item">
        <span className="logout-icon"></span>Logout</div>

        </div>
      </div>

      <div className="content">
        <div className='firstdiv'>
      <div className="info-table-container">
        <table className="info-table">
          <tbody>
            <tr>
                <td className="info-label">Employee Name</td>
                {selectedEmployee &&(
                <td className="info-value">{selectedEmployee.emp_name}</td>
                )} 
            </tr>
            <tr>
                <td className="info-label">Employee ID</td>
                {selectedEmployee &&(
                <td className="info-value">{selectedEmployee.emp_id}</td>
                )} 
            </tr>
            <tr>
                <td className="info-label">Employee Designation</td>
                {selectedEmployee &&(
                <td className="info-value">{selectedEmployee.designation}</td>
                )} 
            </tr>  
            {/* <tr>
              <td className="info-label">Employee Name</td>
              <td className="info-value">Rahul</td>
            </tr>

            <tr>
              <td className="info-label">Employee ID</td>
              <td className="info-value">86215</td>
            </tr>  

            <tr>
              <td className="info-label">Designation</td>
              <td className="info-value">Software Engineer</td>
            </tr> */}

          </tbody>
        </table>
      </div>
      {/* char starts */}
      <div className="bar-chart-container">
          <h2>Leave Statistics</h2>
          <ResponsiveContainer width="90%" height={210}>
            <BarChart data={bar()}>
              <XAxis dataKey="name" />
              <YAxis />
              <CartesianGrid stroke="#ccc" />
              <Legend />
              <Tooltip />
              <Bar dataKey="leave_taken" name="Leave Taken" fill="#ff9900" />
              <Bar dataKey="Paternity_leave" name="Paternity Leave" fill="#8884d8" />
              <Bar dataKey="Sick_leave" name="Sick Leave" fill="#82ca9d" />
              <Bar dataKey="Casual_leave" name="Casual Leave" fill="#ffc658" />
